#!/bin/bash
# Load environment variables from .env file
if [ -f .env ]; then
    set -a
    source .env
    set +a
fi

echo "Starting SQL code generation..."

# Generate models.py using sqlacodegen
{ 
    echo '# NOTE: THIS FILE WAS AUTOMATICALLY GENERATED USING sqlacodegen.
    # DO NOT EDIT THIS FILE MANUALLY!';
    sqlacodegen "postgresql://$POSTGRES_URL";
} > models.py

echo "SQL code generation has been completed successfully."
